import React, { Component } from 'react'
import { Button, Header, Image, Modal, Icon } from 'semantic-ui-react'
import DatePicker from 'react-datepicker'
import "react-datepicker/dist/react-datepicker.css";


class ModalExampleDimmer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,

      startDate: new Date()
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(date) {
    this.setState({
      startDate: date
    });
  }

  show = dimmer => () => this.setState({ dimmer, open: true })
  close = () => this.setState({ open: false })

  render() {
    const { open, dimmer } = this.state

    return (
      <div>
        <Button onClick={this.show(true)}>Default</Button>
        {/* <Button onClick={this.show('inverted')}>Inverted</Button>
        <Button onClick={this.show('blurring')}>Blurring</Button> */}

        <Modal dimmer={dimmer} open={open} onClose={this.close}>
          <Modal.Header>
          <div className='image'>
      <Icon name='trophy' />
      Create and assign a new workout for "mm/dd/yyyy and given time(display here)"
    </div>
          </Modal.Header>
          <Modal.Content image>

            <Image wrapped size='medium' src='http://pluspng.com/img-png/png-exercise-exercise-icon-image-16341-300.png' />
            <Modal.Description>
              <Header>Workout Name:</Header>
              <Form>
                <Form.Field>
                  <input placeholder='Upper body workout' />
                </Form.Field>
                </Form>
              <p>We've found the following gravatar image associated with your e-mail address.</p>
              <p>Is it okay to use this photo?</p>
            </Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button color='black' onClick={this.close}>
              Nope
            </Button>
            <Button
              positive
              icon='checkmark'
              labelPosition='right'
              content="Yep, that's me"
              onClick={this.close}
            />
          </Modal.Actions>
        </Modal>
      </div>
    )
  }
}

export default ModalExampleDimmer
