import React, { Component } from 'react';

import { withFirebase } from '../Firebase';
import { Card, Loader, Button, Message } from 'semantic-ui-react';

class UserItem extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loading: false,
      user: null,
      ...props.location.state,
    };
  }

  componentDidMount() {
    if (this.state.user) {
      return;
    }

    this.setState({ loading: true });

    this.props.firebase
      .user(this.props.match.params.id)
      .on('value', snapshot => {
        this.setState({
          user: snapshot.val(),
          loading: false,
        });
      });
  }

  componentWillUnmount() {
    this.props.firebase.user(this.props.match.params.id).off();
  }

  onSendPasswordResetEmail = () => {
    this.props.firebase.doPasswordReset(this.state.user.email);
    this.state.ok = !this.state.ok;
  };

  onDeleteUser = (uid) => {
    this.props.firebase.user(uid).remove()
  }

  render() {
    const { user, loading, ok  } = this.state;

    return (
      <Card fluid={true}>

      {ok ? (    <Message
    success
    header='Your user registration was successful'
    content='You may now log-in with the username you have chosen'
  />) : null}
        {loading ? (
          <Loader active inline="centered" />
        ) : (
          <Card.Content>
            <Card.Header>User: {user.uid}</Card.Header>
            <Card.Description>
              {user && (
                <div>
                  <Card.Content>
                    <Card.Meta>
                      <span>Username: {user.username}</span>
                    </Card.Meta>
                    <Card.Description>{user.email}</Card.Description>
                    <br />
                    <Button
                      primary
                      type="button"
                      onClick={this.onSendPasswordResetEmail}
                    >
                      Send Password Reset
                    </Button>
                    <Button

                      type="button"
                      onClick={()=>{this.onDeleteUser(user.uid)}}
                    >
                      Disable
                    </Button>
                  </Card.Content>
                </div>
              )}
            </Card.Description>
          </Card.Content>
        )}
      </Card>
    );
  }
}

export default withFirebase(UserItem);
